const productData = [{ Name: "Bread", Price: 2.5, Rating: 4, ID: 0, Description: "Whole grain bread", Supplier: { ID: 1, Name: "Tokyo Traders", }, }, { Name: "Milk", Price: 3.5, Rating: 3, ID: 1, Description: "Low fat milk", Supplier: { ID: 0, Name: "Exotic Liquids", }, }, { Name: "Vint soda", Price: 20.9, Rating: 3, ID: 2, Description: "Americana Variety - Mix of 6 flavors", Supplier: { ID: 0, Name: "Exotic Liquids", }, }, { Name: "Havina Cola", Price: 19.9, Rating: 3, ID: 3, Description: "The Original Key Lime Cola", Supplier: { ID: 0, Name: "Exotic Liquids", }, }, { Name: "Fruit Punch", Price: 22.99, Rating: 3, ID: 4, Description: "Mango flavor, 8.3 Ounce Cans (Pack of 24)", Supplier: { ID: 0, Name: "Exotic Liquids", }, }, { Name: "Cranberry Juice", Price: 22.8, Rating: 3, ID: 5, Description: "16-Ounce Plastic Bottles (Pack of 12)", Supplier: { ID: 0, Name: "Exotic Liquids", }, }, { Name: "Pink Lemonade", Price: 18.8, Rating: 3, ID: 6, Description: "36 Ounce Cans (Pack of 3)", Supplier: { ID: 0, Name: "Exotic Liquids", }, }, { Name: "DVD Player", Price: 35.88, Rating: 5, ID: 7, Description: "1080P Upconversion DVD Player", Supplier: { ID: 1, Name: "Tokyo Traders", }, }, { Name: "LCD HDTV", Price: 1088.8, Rating: 3, ID: 8, Description: "42 inch 1080p LCD with Built-in Blu-ray Disc Player", Supplier: { ID: 1, Name: "Tokyo Traders", }, }, { Name: "Lemonade", Price: 1.01, Rating: 7, ID: 9, Description: "Classic, refreshing lemonade (Single bottle)", Supplier: { ID: 0, Name: "Exotic Liquids", }, }, { Name: "Coffee", Price: 6.99, Rating: 1, ID: 10, Description: "Bulk size can of instant coffee", Supplier: { ID: 0, Name: "Exotic Liquids", }, }, ];
const supplierData = [{ "Name": "Exotic Liquids", "ID": 0 }, { "Name": "Tokyo Traders", "ID": 1 }]
const product = skuid.$M('Products');
const productDrilldown = skuid.$M('ProductsDrilldown');
const suppliers = skuid.$M('Suppliers');
productData.forEach(
    (r) => {
        product.updateRow(product.createRow(), r);
        productDrilldown.updateRow(productDrilldown.createRow(), r);
    }
)
supplierData.forEach(
    (r) => { suppliers.updateRow(suppliers.createRow(), r) }
)

// Element reference
const productsByRating = document.querySelectorAll('div[data-te="skuid__chart"]')[0],
    productsBySupplier = document.querySelectorAll('div[data-te="skuid__chart"]')[1],
    productList = "#product-list",
    drilldownProductList = "#drilldown-product-list",
    supplierDrilldown = '#supplier-drilldown div[role="dialog"]'

let donutChartSegments = productsBySupplier.querySelectorAll('path.highcharts-point');

// We'll be using the same buttons in every step, so let's just set them up once.
// This will make our steps easier to read too!
// Consider setting up other patterns here later on!
const previousButton = {
    classes: "shepherd-button-secondary",
    text: "Previous",
    action() {
        return Shepherd.activeTour.back();
    }
},
    nextButton = {
        text: "Next",
        action() {
            return Shepherd.activeTour.next();
        }
    },
    completeTourButton = {
        text: "Finish",
        action() {
            return Shepherd.activeTour.complete();
        }
    }

// Set up tour options
const tour = new Shepherd.Tour({
    useModalOverlay: true,
    confirmCancel: true,
    confirmCancelMessage: "Are you sure you want to exit the tour?",
    keyboardNavigation: false,
    // These options apply to each step in the tour, but it's also possible to overwrite them.
    defaultStepOptions: {
        cancelIcon: {
            enabled: true
        },
        scrollTo: { behavior: "smooth", block: "center" },
        // Let's set a small offset so dialog boxes don't appear right on top of elements.
        // This is done by setting options for Popper, the library Shepherd uses for generating popups.
        popperOptions: {
            modifiers: [{ name: 'offset', options: { offset: [0, 16] } }]
        }
    }
});

// Now to script the steps of the tour.
// We do this by creating an array of step objects.
let steps = [
    {
        title: "Products dashboard",
        text: `Welcome to your products dashboard!<br/>
        This tour will cover a few of the things you can do from this page.`,
        buttons: [nextButton]
    },
    {
        title: "Product ratings",
        text: `<p>This graph displays product ratings as horizontal bars. This allows you to quickly parse how different products stack up against each other. You can also hover over each bar for more details.</p>

            <p>This graph is also <b>responsive to data changes on this page</b>. If you edit a product's rating in the table below, this graph will automatically update to reflect the new rating.</p>`,
        attachTo: {
            element: productsByRating,
            on: "right"
        },
        buttons: [previousButton, nextButton]
    },
    {
        title: "Products by supplier",
        text: `This graph illustrates the makeup of your products by supplierâ€”particularly useful if you want to make sure you have a diversified portfolio of contracted suppliers. Hover over a segment to see more information.`,
        attachTo: {
            element: productsBySupplier,
            on: "left"
        },
        buttons: [previousButton, nextButton]
    },
    {
        title: "Supplier information",
        text: `This chart also allows you to <b>click</b> individual segments for more information about the products that comprise it.<br\> Click this segment to see an example.`,
        attachTo: {
            element: donutChartSegments[1],
            on: "left"
        },
        when: {
            show: () => {
                donutChartSegments[1].addEventListener('click', () => {
                    Shepherd.activeTour.next()
                        ;
                }, { once: true })
            }
        },
        buttons: [previousButton]
    },
    {
        title: "Supplier segments",
        text: `This modal shows which products are filed under the segment you clicked in the chart.`,
        attachTo: {
            element: supplierDrilldown,
            on: "bottom"
        },
        // Skuid needs a moment to render the dialog modal.
        // This isn't the most graceful way to wait for it,
        // but it works for our example.
        beforeShowPromise: function () {
            return new Promise(function (resolve) {
                setTimeout(function () {
                    resolve();
                }, 400);
            });
        },
        buttons: [nextButton]
    },
    {
        title: "Updating data from the drilldown list",
				// As a sidenote, due to how we're using UI-only models, changes from the drilldown won't affect the product list.
				// However, changes from the product list do affect the drilldown. Obviously this wouldn't be ideal in the real world,
				// and if this were important to describing this script, I'd fix it. Alas! Let's pretend the data works perfectly.
        text: `Since this list is connected to the same data as the larger product list below, changing data in that table will update it here. <br/> <br/> Close the drilldown modal to proceed.`,
        attachTo: {
            element: supplierDrilldown,
            on: "top"
        },
        when: {
            show: () => {
                document.querySelector(supplierDrilldown).querySelector('.fragment__icon').addEventListener('click', () => {
                    Shepherd.activeTour.next()
                        ;
                }, { once: true })
            }
        },
        buttons: [previousButton]
    },

    {
        title: "Product List",
        text: `Finally, this is the full list of individual products. If you updated data within the chart drilldown, it'll be reflected here also. <br/><br/> This table is the best way to view and update the details of all of your products.`,
        attachTo: {
            element: productList,
            on: "top"
        },
        buttons: [previousButton, completeTourButton]
    }
]

// Add all those steps we scripted to our tour
tour.addSteps(steps)
// With the steps added, initiate the tour!
tour.start();
